mapper.select:
SELECT

FROM passage psg


    psg.state = 1 OR psg.state = 2 AND psg.type = #{passageType}
    ORDER BY psg.publish_time DESC
    limit 0,#{showPassageNums}

SELECT count(*)
FROM passage
WHERE type = #{passageType}

SELECT

FROM passage psg


    psg.state = 1 OR psg.state = 2 AND psg.type = #{passageType}
    ORDER BY psg.publish_time DESC
    limit #{fromIndex},#{pageSize}

SELECT count(*)
FROM passage psg


    psg.lesson = #{lessonId} AND psg.chapter = #{chapterId} AND psg.type = #{passageType}

SELECT

FROM passage psg


  psg.state = 1
  OR psg.state = 2
  AND psg.lesson = #{lessonId}
  AND psg.chapter = #{chapterId}
  AND psg.type = #{passageType}
  limit #{fromIndex},#{pageSize}

SELECT

FROM passage


    lesson = #{lessonId}


    AND title LIKE concat(concat('%',#{title}),'%') OR


ORDER BY publish_time DESC
limit #{fromIndex},#{pageSize}

SELECT count(*) FROM passage
lesson = #{lessonId}
AND title LIKE concat(concat('%',#{title}),'%')
