mapper.select:
select

from teacher


		id = #{id,jdbcType=INTEGER}


		name = #{name,jdbcType=VARCHAR}


		AND direction = #{direction,jdbcType=VARCHAR}


		AND subject = #{subject,jdbcType=VARCHAR}


		AND pic = #{pic,jdbcType=VARCHAR}


		AND school_id = #{school_id,jdbcType=VARCHAR}

select * from teacher



         name like #{name,jdbcType=VARCHAR}


          or direction like #{direction,jdbcType=VARCHAR}


          or subject like #{subject,jdbcType=VARCHAR}
